            <Topics path="/topics" />
            <Users path="/users" />
            <SingleArt path="/articles/:article_id" />
            <SingleUser path="/user/:user" />
            <PostArticle path="/postarticle" />
            <Error path="/error" />
          </Router>{" "}
        </SignIn>
        {/*}*/}
      </Fragment>
    );
  }
}

export default App;
