import React, { Component } from "react";
import Error from "./Error";
import { getUsers } from "../Api";

class SignIn extends Component {
  state = {
    username: null,
    errStatus: false
  };

  handleChange = event => {
    this.setState({ username: event.target.value });
  };

  handleSignIn = (event, username) => {
    event.preventDefault();
    getUsers(username)
      .then(this.setState({ username, loggedIn: true }))
      .catch(err => {
        console.dir(err) ||
          this.setState({
            errStatus: {
              message:
                err.response.data.message || "Sorry this username is invalid",
              status: err.response.request.status || 400
            },
            replace: true
          });
      });
    window.localStorage.setItem("username", username);
  };

  render() {
    const { errStatus, loggedIn } = this.state;

    if (loggedIn) return this.props.children;
    if (errStatus) return <Error errStatus={errStatus} />;
    return (
      <div className="SignIn">
        <header className="Sign-in-header" />
        <h1>Welcome to NC News!</h1>
        <h4>Please enter your username</h4>
        <form onSubmit={this.handleSignIn}>
          <input
            name="username"
            placeholder="User e.g. grumpy19"
            onChange={this.handleChange}
            required
          />
          <button type="submit">Submit</button>
        </form>
        <br />
      </div>
    );
  }
}

export default SignIn;
