import axios from "axios";

export const url = "https://afternoon-beyond-70751.herokuapp.com/api/";

export const getArticles = (p, sort_by, order, topic) => {
  return axios
    .get(`${url}articles`, {
      params: {
        p,
        sort_by,
        order,
        topic
      }
    })
    .then(({ data }) => data);
};
