import React, { Component, Fragment } from "react";
import "./styling/App.css";
import { Router } from "@reach/router";
import Home from "./components/Home";
import Topics from "./components/Topics";
import Users from "./components/Users";
import SignIn from "./components/SignIn";
import SingleArt from "./components/SingleArt";
import PostArticle from "./components/PostArticle";
import SingleUser from "./components/SingleUser";
import Error from "./components/Error";
import { getUsers } from "./Api";
import { navigate } from "@reach/router";

class App extends Component {
  state = {
    username: null,
    errStatus: false,
    loggedIn: false
  };

  handleSignIn = (event, username) => {
    event.preventDefault();
    getUsers(username)
      .then(() => {
        this.setState({ username, loggedIn: true });
        navigate("/home");
      })
      .catch(err => {
        console.dir(err) ||
          this.setState({
            errStatus: {
              message:
                err.response.data.message || "Sorry this username is invalid",
              status: err.response.request.status || 400
            },
            replace: true
          });
      });
    window.localStorage.setItem("username", username);
  };

  render() {
    const { username, loggedIn } = this.state;
    console.log(username);
    return (
      <Fragment>
        <SignIn
          path="/"
          handleSignIn={this.handleSignIn}
          loggedIn={loggedIn}
          username={username}
        >
          <Router username={username}>
            <Home path="/home" />
            <Home path="articles/topic/:topic" />
            <Home path="articles/topic/:slug" />
            <Topics path="/topics" />
            <Users path="/users" />
            <SingleArt path="/articles/:article_id" username={username} />
            <SingleUser path="/user/:user" />
            <PostArticle path="/postarticle" />
            <Error default />
          </Router>{" "}
        </SignIn>
      </Fragment>
    );
  }
}

export default App;
