          <Topics path="/topics" username={username} />
          <Users path="/users" username={username} />
          <SingleArt path="/articles/:article_id" username={username} />
          <SingleUser path="/user/:user" username={username} />
          <PostArticle path="/postarticle" username={username} />
          <Error default />
        </Router>{" "}
        {/* </SignIn> */}
        {/*}*/}
      </Fragment>
    );
  }
}

export default App;
