import React from "react";
import { Link } from "@reach/router";

export default function NavButtons(article, comment) {
  const postStyle = { float: "right" };
  return (
    <div>
      <Link to="/home">
        <button>Home</button>
      </Link>
      <Link to="/topics">
        <button>Topics</button>
      </Link>
      <Link to="/users">
        <button>Users</button>
      </Link>
      {article && (
        <Link to="/postarticle">
          <button style={postStyle}>Post Article</button>
        </Link>
      )}
      {topic && (
        <Link to="/postcomment">
          <button style={postStyle}>Post Topic</button>
        </Link>
      )}
    </div>
  );
}
