import React, { Component } from "react";
import Articles from "./Articles";
import { getArticles } from "../Api";
import "../styling/App.css";
import NavButtons from "./NavButtons";
import QuerySelector from "./QuerySelector";
import { Link } from "@reach/router";
import { navigate } from "@reach/router";

class Home extends Component {
  state = {
    username: this.state,
    page: 1,
    sort: null,
    order: null,
    errStatus: false,
    isLoading: true,
    articles: []
  };

  componentDidMount() {
    const { page, sort, order } = this.props;
    console.log("component mounted!");
    getArticles(page, sort, order)
      .then(data => this.setState({ articles: data.articles }))
      .catch(err => {
        this.setState({ errStatus: true });
      });
  }

  handlePageSubmit = (inc, event) => {
    event.preventDefault();
    const { page } = this.state;
    this.setState({ page: page + inc });
    console.log(this.state);
  };

  componentDidUpdate(prevProps, prevState) {
    const { page, sort, order } = this.state;

    if (prevState.page !== page) {
      getArticles(page, sort, order)
        .then(data => this.setState({ articles: data.articles }))
        .catch(err => {
          this.setState({ errStatus: true });
        });
    }
  }

  render() {
    const articleItems = this.state.articles.map(article => {
      if (errStatus) return navigate("/error");
      else
        return (
          <div className="Articles" key={article.article_id}>
            <Link to={`/articles/${article.article_id}`}>
              <h2 className="Article-title">{article.title}</h2>
            </Link>
            <div className="Author">
              <Link to={`/articles/topic/${article.topic}`}>
                <p>Topic: {article.topic}</p>
              </Link>
              <Link to={`/user/${article.author}`}>
                <p>Author: {article.author}</p>
              </Link>
              <p>
                Created:{" "}
                {article.created_at.slice(0, 19).replace(/[a-zA-Z]/, " At: ")}
              </p>
              {/* 2016-08-18T12:07:52.389Z */}
            </div>
            <p className="Article-body">{article.body.slice(0, 250)}...</p>
            <hr />
            <small className="Comments">
              <Link to={`/articles/${article.article_id}#comments`}>
                {" "}
                Comments: {article.comment_count}{" "}
              </Link>
            </small>{" "}
            <hr />
          </div>
        );
    });
    return (
      <div className="Home">
        <header className="Home-header">
          <h1 className="Home-title">NC News</h1>
          <h2 className="Welcome">Welcome to NC News {this.props.username}</h2>
          <hr />
          <hr />
        </header>
        <NavButtons />
        <hr />
        <QuerySelector />
        <div>
          <h1 className="ArtText">Articles</h1>
          {articleItems}
        </div>
        <div className="Page-button">
          <button
            type="submit"
            onSubmit={() => this.handlePageSubmit(-1)}
            disabled={this.state.page === 1 ? true : false}
          >
            {/* disalbed=if page num = 1 */}
            &#171;
          </button>{" "}
          1{" "}
          <button type="submit" onSubmit={() => this.handlePageSubmit(1)}>
            &#187;
          </button>
        </div>
      </div>
    );
  }
}

Home.propTypes = {};

export default Home;
